import styled from 'styled-components';

export const Navbar = styled.div`
  @keyframes anim {
    0% {
      color: #ff0;
    } /*Amarillo*/
    25% {
      color: #f80;
    } /*Naranja*/
    50% {
      color: #000;
    } /*Negro*/
    75% {
      color: #f80;
    } /*Otra vez naranja*/
    100% {
      color: #ff0;
    } /*Otra vez amarillo*/
  }
  .logo {
    animation-name: anim;
    animation-duration: 5s;
    animation-iteration-count: infinite;
    float: left;
    font-size: 50%;
    color: black;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
    font-family: 'VremenaGroteskRegular', sans-serif;
  }
  .navbar {
    overflow: hidden;
    background-color: #333;

    border: solid green;
  }
  .opciones {
    text-align: right;
    font-family: 'VremenaGroteskRegular', sans-serif;
    border: solid red;
    padding-left: 40%;
    height: 20%;
  }
  .opcion {
    float: left;
    font-size: 50%;
    color: black;
    text-align: center;
    padding: 14px 16px;
    text-decoration: none;
  }

  .dropdown {
    float: left;
    overflow: hidden;
  }

  .dropdown .dropbtn {
    font-size: 50%;
    border: none;
    outline: none;
    color: black;
    padding: 14px 16px;
    background-color: inherit;
    font-family: inherit;
    margin: 0;
  }

  .opcion:hover,
  .dropdown:hover .dropbtn {
    border-bottom: solid black;
  }

  .dropdown-content {
    display: none;
    position: absolute;
    background-color: #f9f9f9;
    min-width: 160px;
    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
    z-index: 1;
  }

  .dropdown-content a {
    float: none;
    color: black;
    padding: 12px 16px;
    text-decoration: none;
    display: block;
    text-align: left;
  }

  .dropdown-content a:hover {
    background-color: #ddd;
  }

  .dropdown:hover .dropdown-content {
    display: block;
  }
`;
