import styled from 'styled-components';

export const Navbar = styled.section`
  .navigation {
    height: $nav-height;
    background: $nav-background;
  }
  .brand {
    position: absolute;
    padding-left: 20px;
    float: left;
    line-height: $nav-height;
    text-transform: uppercase;
    font-size: 1.4em;
    a,
    a:visited {
      color: $nav-font-color;
      text-decoration: none;
    }
    .nav-container {
      max-width: $content-width;
      margin: 0 auto;
    }
  }
`;
export const Nav = styled.div`
  .nav-container {
    max-width: $content-width;
    margin: 0 auto;
  }

  .navi {
    float: right;
    ul {
      list-style: none;
      margin: 0;
      padding: 0;
      li {
        float: left;
        position: relative;
        a,
        a:visited {
          display: block;
          padding: 0 20px;
          line-height: $nav-height;
          background: $nav-background;
          color: $nav-font-color;
          text-decoration: none;
          &:hover {
            background: $link-hover-color;
            color: $nav-font-color;
          }
          &:not(:only-child):after {
            padding-left: 4px;
            content: ' â–¾';
          }
        } // Dropdown list
        ul li {
          min-width: 190px;
          a {
            padding: 15px;
            line-height: 20px;
          }
        }
      }
    }
  }

  // Dropdown list binds to JS toggle event
  .nav-dropdown {
    position: absolute;
    display: none;
    z-index: 1;
    box-shadow: 0 3px 12px rgba(0, 0, 0, 0.15);
  }
`;
