import styled from 'styled-components';

export const Navbar = styled.div`
  @keyframes anim {
    0% {
      color: rgb(13.224.183);
    } /*Amarillo*/
    25% {
      color: #f80;
    } /*Naranja*/
    50% {
      color: #000;
    } /*Negro*/
    75% {
      color: #f80;
    } /*Otra vez naranja*/
    100% {
      color: #ff0;
    } /*Otra vez amarillo*/
  }
  .logo {
    animation-name: anim;
    animation-duration: 5s;
    animation-iteration-count: infinite;
    float: left;
    font-size: 2.1em;
    color: black;
    text-align: center;
    padding: 0.5em 2.5em;
    font-weight: 550;
    text-decoration: none;
    font-family: 'VremenaGroteskRegular', sans-serif;
  }
  .navbar {
    overflow: hidden;
    background-color: #333;
    border: solid green;
  }
  .opciones {
    text-align: right;
    font-family: 'VremenaGroteskRegular', sans-serif;
    height: 5em;
    box-shadow: 0 15px 10px -10px rgba(0, 0, 0, 0.3),
      0 1px 4px rgba(0, 0, 0, 0.2), 0 0 40px rgba(0, 0, 0, 0.1) inset;
    padding-left: 47em;
    font-weight: 640;
    margin: -0.5em;
  }
  .opcion {
    float: left;
    font-size: 1em;
    color: black;
    text-align: center;
    padding: 1em 2.5em;
    text-decoration: none;
    margin-top: 1em;
  }

  .dropdown {
    float: left;
    overflow: hidden;
  }

  .dropdown .dropbtn {
    font-size: 1em;
    font-weight: 640;
    border: none;
    outline: none;
    color: black;
    padding: 1em 2.5em;
    background-color: inherit;
    font-family: inherit;
    margin-top: 1em;
  }

  .opcion:hover,
  .dropdown:hover .dropbtn {
    border-bottom: solid black;
  }

  .dropdown-content {
    display: none;
    position: absolute;
    background-color: #f9f9f9;
    min-width: 160px;
    box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
    z-index: 1;
  }

  .dropdown-content a {
    float: none;
    color: black;
    padding: 12px 16px;
    text-decoration: none;
    display: block;
    text-align: left;
  }

  .dropdown-content a:hover {
    background-color: #ddd;
  }

  .dropdown:hover .dropdown-content {
    display: block;
  }
`;
